import * as React from 'react';
import { alpha } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import TableSortLabel from '@mui/material/TableSortLabel';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import Checkbox from '@mui/material/Checkbox';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import DeleteIcon from '@mui/icons-material/Delete';
import { visuallyHidden } from '@mui/utils';
import AssignmentRoundedIcon from '@mui/icons-material/AssignmentRounded';
import SearchBar from "material-ui-search-bar";
import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';
import AddCircleOutlineRoundedIcon from '@mui/icons-material/AddCircleOutlineRounded';
import Divider from '@mui/material/Divider';
import { HostsInfoLanding } from './HostsViewLanding';
import { HostsEditLanding } from './HostsEditLanding';
import { useNavigate } from 'react-router-dom';
import { json } from 'stream/consumers';
import { Axios } from 'axios';
import axios from 'axios';

export interface HostsData{
  _id:               string;
  name:              string;
  description:       string;
  OS:                string;
  Running_services:  number;
  Running_instances: number;
  health_status:     string;
  services:          Service[];
  labels:            string[];
  number_of_cores:   number;
  processor_type:    string;
  memory:            string;
  total_storage:     string;
  storage_mounts:    StorageMount[];
}

export interface Service {
  Name:      string;
  Instances: number;
}

export interface StorageMount {
  Mount_point: string;
  Storage:     string;
}


const deleteHost =async (data) =>{


  try {

      const response = await axios({
      method: "put",
      url: "http://127.0.0.1:8000/api/hosts/",
      data: JSON.stringify(data),
      headers: { "Content-Type": "multipart/form-data" },
    });
    console.log(response);

  } catch(error) {

    console.log(error);
  }

};

function descendingComparator<T>(a: T, b: T, orderBy: keyof T) {
  if (b[orderBy] < a[orderBy]) {
    return -1;
  }
  if (b[orderBy] > a[orderBy]) {
    return 1;
  }
  return 0;
}

type Order = 'asc' | 'desc';

function getComparator<Key extends keyof HostsData>(
  order: Order,
  orderBy: Key,
): (
  // a: { [key in Key]: number | string |string[]|Service},
  // b: { [key in Key]: number | string |string[]|Service},
  a: HostsData, b: HostsData
) => number {
  return order === 'desc'
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

// This method is created for cross-browser compatibility, if you don't
// need to support IE11, you can use Array.prototype.sort() directly
function stableSort<T>(array: readonly T[], comparator: (a: T, b: T) => number) {
  const stabilizedThis = array.map((el, index) => [el, index] as [T, number]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) {
      return order;
    }
    return a[1] - b[1];
  });
  return stabilizedThis.map((el) => el[0]);
}

interface HeadCell {
  disablePadding: boolean;
  id: keyof HostsData;
  label: string;
  numeric: boolean;
}

const headCells: readonly HeadCell[] = [
  {
    id: 'name',
    numeric: false,
    disablePadding: false,
    label: 'Name',
  },
  {
    id: 'Running_services',
    numeric: true,
    disablePadding: false,
    label: '# of services,containers',
  },
  {
    id: 'services',
    numeric: false,
    disablePadding: false,
    label: 'Services',
  },
  {
    id: 'health_status',
    numeric: false,
    disablePadding: false,
    label: 'Health Status',
  }
];

interface EnhancedTableProps {
  numSelected: number;
  onRequestSort: (event: React.MouseEvent<unknown>, property: keyof HostsData) => void;
  onSelectAllClick: (event: React.ChangeEvent<HTMLInputElement>) => void;
  order: Order;
  orderBy: string;
  rowCount: number;
}

function EnhancedTableHead(props: EnhancedTableProps) {
  const { onSelectAllClick, order, orderBy, numSelected, rowCount, onRequestSort } =
    props;
  const createSortHandler =
    (property: keyof HostsData) => (event: React.MouseEvent<unknown>) => {
      onRequestSort(event, property);
    };



  return (


    <TableHead>
      <TableRow>
        <TableCell padding="checkbox">
          <Checkbox
            color="primary"
            indeterminate={numSelected > 0 && numSelected < rowCount}
            checked={rowCount > 0 && numSelected === rowCount}
            onChange={onSelectAllClick}
            inputProps={{
              'aria-label': 'select all hosts',
            }}
          />
        </TableCell>
        {headCells.map((headCell) => (
           headCell.id !=='services'?(
          <TableCell
            key={headCell.id}
            align= 'left' //{headCell.numeric ? 'right' : 'left'}
            padding={headCell.disablePadding ? 'none' : 'normal'}
            sortDirection={orderBy === headCell.id ? order : false}

          >
            <TableSortLabel
              active={orderBy === headCell.id}
              direction={orderBy === headCell.id ? order : 'asc'}
              onClick={createSortHandler(headCell.id)}
            >
              {headCell.label}
              {orderBy === headCell.id ? (
                <Box component="span" sx={visuallyHidden}>
                  {order === 'desc' ? 'sorted descending' : 'sorted ascending'}
                </Box>
              ) : null}
            </TableSortLabel>
          </TableCell>):(
            <TableCell
            key={headCell.id}

            align= 'left' //{headCell.numeric ? 'right' : 'left'}
            padding={headCell.disablePadding ? 'none' : 'normal'}>
           {headCell.label}

           </TableCell>
          )

        ))}
         <TableCell>
            Actions

      </TableCell>
      </TableRow>
    </TableHead>

  );
}


export const HostsTable : React.FC<{data: HostsData[]}> = ({ data }) => {

  const navigate=useNavigate();
  const routeChangeView = (name:string) =>{ 
    const path = `/app/controlcenter/HostsInfo`; 
    navigate(path,{state:{id:name}});
  }

  const routeChangeEdit=(name:string)=>{
    const path=`/app/controlcenter/EditHosts`;
    navigate(path,{state:{id:name}})
  }


  const [orderBy, setOrderBy] = React.useState<keyof HostsData>('name');
  const [order, setOrder] = React.useState<Order>('asc');
  const [selected, setSelected] = React.useState<readonly string[]>([]);
  const [page, setPage] = React.useState(0);
  const [dense, setDense] = React.useState(true);
  const [rowsPerPage, setRowsPerPage] = React.useState(5);
  const [searched, setSearched] = React.useState<string>("");
  const [rows1,setRows1]=React.useState<HostsData[]>(data);

  const [numSelected,setNums]= React.useState(0);


  React.useEffect(() => {
    setRows1(data);
    setNums(selected.length)
}, [data,selected.length])



  const requestSearch = (searchedVal: string) => {
      const filteredRows = data.filter((row) => {
        return row.name.toLowerCase().includes(searchedVal.toLowerCase());
      });
      setRows1(filteredRows);
    };

    const cancelSearch = () => {
      setSearched("");
      requestSearch(searched);
    };
  const handleRequestSort = (
    event: React.MouseEvent<unknown>,
    property: keyof HostsData,
  ) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const handleSelectAllClick = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.checked) {
      const newSelecteds = rows1.map((n) => n.name);
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  };

  const handleClick = (event: React.MouseEvent<unknown>, name: string) => {
    const selectedIndex = selected.indexOf(name);
    let newSelected: readonly string[] = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, name);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1),
      );
    }

    setSelected(newSelected);
  };

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleHostsView =(event:React.MouseEvent<unknown>,name:string)=>{
    console.log(name+"View");
    event.preventDefault();
    event.stopPropagation();
    routeChangeView(name);
  };

  const handleHostsDelete=(event:React.MouseEvent<unknown>,hostobj:HostsData)=>{
    console.log("Delete");
    console.log(JSON.stringify(hostobj));
    if(JSON.stringify(hostobj['services'])===JSON.stringify([])){
      alert('host has no service running on it. Deletion will be done');
      deleteHost()

    }
    else{
      alert('There are services running on the host. DEletion is not possible');
    }
    event.preventDefault();
    event.stopPropagation();

  }
  const routeChangeAddHosts= () => {
    const path = `/app/controlcenter/AddHosts`;
    navigate(path);
  };

  const handleHostsEdit=(event:React.MouseEvent<unknown>,name:string)=>{
    console.log(name+"Edit");
    event.preventDefault();
    event.stopPropagation();
    routeChangeEdit(name);
  };

  const isSelected = (name: string) => selected.indexOf(name) !== -1;

  // Avoid a layout jump when reaching the last page with empty rows.
  const emptyRows =
    page > 0 ? Math.max(0, (1 + page) * rowsPerPage - data.length) : 0;

  const getServices=(row:Service[])=>{
    let str="";
    for(let i=0;i<row.length;i++){
      str=str+row[i].Name+'('+JSON.stringify(row[i].Instances)+')';
      if(i!==row.length-1)
        str=str+',';

    }
    return str;
  }

  return (
    <Box sx={{ width: '100%' }}>
      <Paper sx={{ width: '100%', mb: 2 }}>
        {/* <EnhancedTableToolbar numSelected={selected.length} /> */}
        <Toolbar
      sx={{
        pl: { sm: 2 },
        pr: { xs: 1, sm: 1 },
        ...(numSelected > 0 && {
          bgcolor: (theme) =>
            alpha(theme.palette.primary.main, theme.palette.action.activatedOpacity),
        })
      }}
    >
      {numSelected > 0 ? (
        <Typography
          sx={{ flex: '1 1 100%' }}
          color="inherit"
          variant="subtitle1"
          component="div"
        >
          {numSelected} selected
        </Typography>
      ) : (

        <Typography
          sx={{ flex: '1 1 100%' }}
          variant="h6"
          id="tableTitle"
          component="div"
        >
         Hosts
        </Typography>


      )}
      {numSelected > 0 ? (
        <Tooltip title="Delete">
          <IconButton >
            <DeleteIcon />
          </IconButton>
        </Tooltip>

      ) : (
        <>
        {/* <SearchBar
        value={searched}
        onChange={(searchVal) => requestSearch(searchVal)}
        onCancelSearch={() => cancelSearch()}

        /> */}
       <Tooltip title="Add Host">
          <IconButton onClick={routeChangeAddHosts}>
           <AddCircleOutlineRoundedIcon/>
          </IconButton>
        </Tooltip>
        </>

      )}
    </Toolbar>
        {/* <span style={{marginLeft:'.001rem'}}/> */}
        <Paper sx={{ width:'20%',textAlign:'center'}}>



        </Paper>
        {/* <span style={{marginLeft:'.5rem'}}/> */}
        <TableContainer>
          <Table
            sx={{ minWidth: 70}}
            aria-labelledby="tableTitle"
            size={dense ? 'small' : 'medium'}
          >
            <EnhancedTableHead
              numSelected={selected.length}
              order={order}
              orderBy={orderBy}
              onSelectAllClick={handleSelectAllClick}
              onRequestSort={handleRequestSort}
              rowCount={rows1.length}
            />
            <TableBody>
              {/* if you don't need to support IE11, you can replace the `stableSort` call with:
              rows.slice().sort(getComparator(order, orderBy)) */}
              {rows1.slice().sort(getComparator(order, orderBy))
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((row, index) => {
                  const isItemSelected = isSelected(row.name);
                  const labelId = `enhanced-table-checkbox-${index}`;

                  return (
                    <TableRow
                      hover
                      onClick={(event) => handleClick(event, row.name)}
                      role="checkbox"
                      aria-checked={isItemSelected}
                      tabIndex={-1}
                      key={row.name}
                      selected={isItemSelected}
                      //   sx={{padding:"checkbox"}}
                      sx={{ '&:last-child td, &:last-child th': { border: 0 }}}

                    >
                      <TableCell padding="checkbox">
                        <Checkbox
                          color="primary"
                          checked={isItemSelected}
                          inputProps={{
                            'aria-labelledby': labelId,
                          }}
                        />
                      </TableCell>
                      <TableCell
                        component="th"
                        id={labelId}
                        scope="row"
                        padding="checkbox"
                        align="left"
                      >
                        {row.name}
                      </TableCell>

                      <TableCell align="left">{JSON.stringify(row.Running_services)+'/'+JSON.stringify(row.Running_instances)}</TableCell>
                      <TableCell align="left">{getServices(row.services)}</TableCell>
                      <TableCell align="left">{row.health_status}</TableCell>

                      <TableCell align="left">

                        <Tooltip title="Hosts Detail">
                          <IconButton
                          onClick={(event) => handleHostsView(event, row.name)}
                          >
                            <VisibilityIcon sx={{ fontSize: 20 }}/>
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Edit">
                          <IconButton 
                          onClick={(event) => handleHostsEdit(event, row.name)}
                          >
                            <EditIcon sx={{ fontSize: 20 }}/>
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="delete host">
                          <IconButton 
                           onClick={(event) => handleHostsDelete(event, row)}
                          >
                            <DeleteIcon/>
                          </IconButton>
                        </Tooltip>
                      </TableCell>
                    </TableRow>
                  );
                })}
              {emptyRows > 0 && (
                <TableRow
                  style={{
                    height: (dense ? 33 : 53) * emptyRows,
                  }}
                >
                  <TableCell colSpan={6} />
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={rows1.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>

    </Box>
  );
}
